//Some other component using HttpClient to load the data or create data
import { Component, OnInit} from '@angular/core';
import { DownloadToolService } from 'src/app/library/download-tool/download-tool.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-other-base',
  templateUrl: './topic-other.component.html',
  styleUrls: ['./topic-other.component.css'],
})
export abstract class OtherComponent {

  jsonPath: string = 'assets/json/';
  allData : any[] = []
  fileNames : string[] = ['someName','someOtherName']; 
  constructor(private downloadTool: DownloadToolService,private http: HttpClient) {}

  //Get the  Data from Json files
  getJsonData(): any[] {
    //Check it the files were already loaded if so return them
    if (this.allData != null && this.allData.length &gt 0) {
      return this.allData;
    }
    // Get all the files that are in the file names
    for (let i = 0; i &lt this.fileNames.length; i++) {
      this.http
        .get&ltany&gt(`${this.jsonPath}${this.fileNames[i]}.json`)
        .subscribe((data) =&gt {          
         this.allData.push(data);         
        });
    }
    return this.allData;
  }  
  //Create the json file with the given data and the given file name
  downloadAsJsonFile(data:any,fileName:string): void {
    this.downloadTool.DownloadTextToFileAsJson(data,fileName);
  }
}
</pre></div>

